"""
sesli modülüdür.
"""

def sesli_harfler(metin):

    sesliler_kumesi = set()

    sesliler = 'aeiou' + 'ıöü'

    try:
        for harf in metin:
            # eğer harf sesliler'de varsa -> seslidir
            # eğer harf alfanumerik ise -> str.isalpha()
            if harf in sesliler and harf.isalpha():
                sesliler_kumesi.add(harf)
    except:
        pass
    finally:
        return sesliler_kumesi
